import React, { useState, useEffect } from 'react'
import { db } from '../backend/firebase'
import { collection, addDoc } from "firebase/firestore"


const Add = () => {

    const userCollectionRef = collection(db ,"User");

    const [newName, setnewName] = useState();
    const [newUsername, setnewUsername] = useState();
    const [newPass, setnewPass] = useState();
    const [newClass, setnewClass] = useState();
    const [newDept, setnewDept] = useState();
    const [newCategory, setnewCategory] = useState("open");

    const createUser = async () => {
        await addDoc(userCollectionRef, { 
            username: newUsername,
            name: newName ,
            password: newPass, 
            class: newClass,
            department: newDept,
            category:newCategory
        }).then(function(){
            alert("Data Saved...!")
        })
        .catch(function (error) {
            console.error("Error writing Value: ", error);
          });
    }

    


    return (
        <>
            <div className="container-fluid d-flex justify-content-center align-items-center ">
                <div className=" d-flex flex-column justify-content-center align-items-center">
                    <div className="div">
                        <p><label for="username">Username</label></p>
                        <p><input type="text" name="username" id="username" 
                            onChange={(e) => {
                                setnewUsername(e.target.value);
                            }}/></p>
                    </div>
                   
                    <div className="div">
                        <p><label for="password">Password</label></p>
                        <p><input type="text" name="password" id="password" 
                        onChange={(e) => {
                            setnewPass(e.target.value);
                        }}/></p>
                    </div>
                    <div className="div">
                        <p><label for="name">Name</label></p>
                        <p><input type="text" name="name" id="name" 
                            onChange={(e) => {
                                setnewName(e.target.value);
                            }}/></p>
                    </div>
                    <div className="div">
                        <p><label for="class">Class</label></p>
                        <p><input type="text" name="class" id="class" 
                        onChange={(e) => {
                            setnewClass(e.target.value);
                        }}/></p>
                    </div>
                    <div className="div">
                        <p><label for="department">Department</label></p>
                        <p><input type="text" name="department" id="department"
                        onChange={(e) => {
                            setnewDept(e.target.value);
                        }}/></p>
                    </div>
                    <div className="div">
                        <p><label for="category">Category</label></p>
                        {/* <p><input type="text" name="category" id="category" /></p> */}
                        <p><select className="px-5 py-1" type="text" id="category" name="category" 
                        // onChange={(e) => {
                        //     setnewCategory(e.target.value);
                        // }} 
                        >
                            <option name="category" value="open">Open</option>
                            <option name="category" value="obc">OBC</option>
                            <option name="category" value="nt">NT</option>
                            <option value="sc">SC</option>
                            <option value="tfws">TFWS</option>
                            </select></p>
                    </div>
                    <div className="div">
                        <p><input className="btn btn-primary " type="submit" name="submit" value="Submit" 
                        onClick={createUser}
                        /></p>
                    </div>
                </div>
            </div>
        </>
    )
}

export default Add
