import React from 'react'
import '../index.css';
import {useHistory} from "react-router-dom"


// razorpay
function loadScript(src) {
  return new Promise(resolve => {
    
    const script = document.createElement('script')
    script.src = src
    document.body.appendChild(script)
    script.onload = () => {
      resolve(true)
    }
    script.onerror= () => {
      resolve(false)
    }
    document.body.appendChild(script)
  })
}

//


  const routes = [
    {
      path: "/dashboard",
      exact: true,
      main: () => <h2>Home</h2>
    },
    {
      path: "/add",
      main: () => <h2>add</h2>
    },
    {
      path: "/delete",
      main: () => <h2>delete</h2>
    }
  ];

const Udashboard = () => {

  let history = useHistory();

  const [name,  setname] = useState('Hemant')

async function displayrazorpay() {

    const res = await loadScript("https://checkout.razorpay.com/v1/checkout.js")

    if(!res) {
      alert('Razorpay SDK failed to load !')
      return
    }


    const data = await axios.post('http://localhost:5000/razorpay')

    
    
    if(!data) {
      alert('FAILED TO LOAD DATA !')
      return
    }
    console.log(data);

    const options = {
      key: "rzp_test_92sXJEdsMzESab", 
      amount: data.amount,
      currency: data.currency,
      order_id: data.id,
      name: "Paymentor",
      description: "Test Transaction",
      image: "http://localhost:5000/logo.svg",
      order_i: "order_9A33XWu170gUtm", 
      handler: function (response){
          alert(response.razorpay_payment_id);
          alert(response.razorpay_order_id);
          alert(response.razorpay_signature)
      },
      "prefill": {
          "name": name,     
      }
      
  };
  const paymentObj = new window.Razorpay(options);

  paymentObj.open()
}


    return (
        <>
            <div className="container">
            
                <div className="d-flex justify-content-between my-4">
                    <h4 className="">Paymentor</h4>
                    <button type="button" class="btn btn-outline-primary rounded-pill"
                    onClick={
                      () => {
                          history.push('/')
                      } }>Sign Out</button>
                </div>

                <div className="row">
                    <div className="box1 vh-100  col-lg-2">
                        

                    </div>
                    <div className="box2 vh-100 col-lg-10 d-flex justify-content-center align-items-center">
                        {/* <a href="https://pages.razorpay.com/pl_H8GYbd9C2dDq7W/view" data-url="https://pages.razorpay.com/pl_H8GYbd9C2dDq7W/view" class="banner-button btn rounded-pill btn-outline-primary btn-lg px-5" href="https://rzp.io/l/gIJ8gSwEB" role="button">pay</a> */}

                        <button type="submit" className="btn btn-primary rounded-pill px-4 py-1">Pay</button>

                    </div>
                </div>
            </div>
        </>
    )
}

export default Udashboard
