import '../index.css';
import { Link, useHistory } from "react-router-dom"
import React, { useRef, useState, useEffect } from "react"
import { db } from '../backend/firebase'
import { collection, getDocs } from "firebase/firestore"


// import {BrowserRouter as Router, Route, Switch} from "react-router-dom"
// import { useForm } from "react-hook-form";
// import { yupResolver } from "@hookform/resolvers/yup";
// import * as yup from "yup";

// const schema = yup.object().shape({
//     username: yup.string().required(),
//     password: yup.string().min(4).max(8).required()
// })



const Login = () => {


    const userRef = useRef()
    const passwordRef = useRef()
    // const { login } = useAuth()
    const [error, setError] = useState("")
    const [loading, setLoading] = useState(false)
    const history = useHistory()

    const [users, setUsers] = useState([]);
    const userCollectionRef = collection(db ,"User");

    const [currentUsername, setcurrentUsername] = useState()

    useEffect(() => {
    
    const getUsers = async () => {
        const data = await getDocs(userCollectionRef)
        setUsers(data.docs.map((doc) => ({...doc.data(), id: doc.username})))
        }

        getUsers()
    }, [])
    // emailRef.current.value, passwordRef.current.value
    
    const loginuser = (id , pass) => {
        setcurrentUsername( users.filter((user) => {
            return (id === user.username)
        })
        )

        setcurrentUsername( users.filter((user) => {
            return (pass == user.password)
        })
        )
        console.log(currentUsername);

    }

    async function handleSubmit(e) {
        e.preventDefault()

        try {
        setError("")
        setLoading(true)
        await loginuser(userRef.current.value, passwordRef.current.value)
        history.push("/userdashboard")
        } catch {
        setError("Failed to log in")
        // alert("Enter Valid Username & Password !")
        }

        setLoading(false)
    }
    


    // async function handleSubmit(e) {
    //     e.preventDefault()

    //     try {
    //     setError("")
    //     setLoading(true)
    //     await login(userRef.current.value, passwordRef.current.value)
    //     history.push("/")
    //     } catch {
    //     setError("Failed to log in")
    //     }

    //     setLoading(false)
    // }


//     const { register, handleSubmit, errors } = useForm({
//     resolver: yupResolver(schema),
//   });

//     const onSubmit = (data) => {
//         console.log(data);
//     };

                        
    return (
        <>
           {/* <div className="main vh-100" style={{backgroundImage:"url(./images/Background1.svg)"}}> */}
               
               <div className="bgcolor row vh-100">
                
                    <div className="part1 col-md-6 d-flex justify-content-center align-items-center">
                        
                        <h1 className="title fw-bold"><span className="fw-light">ADCET </span> Paymentor</h1>
                        
                    </div>
                    
                    <div className="part2 col-md-6 bg-white br d-flex flex-column justify-content-start justify-content-md-center align-items-center">
                   
                        
                          <button type="button" className="btn btn-outline-primary rounded-pill position-absolute top-0 end-0 mx-4 my-4" 
                          onClick={() => {history.push('/adminlogin')}} >Admin Login</button>
                    
                    
                        <div className="form">
                                <div className="l-title">
                                    <h5 className="fw-bold mb-4 fs-3">Log in</h5>
                                </div>
                                {/* action="#" method="post" */}
                            <form className="d-flex flex-column align-items-center"   >
                            
                                    <div class="form-group mb-3">
                                        <input type="email" class="form-control"  placeholder="username" ref={userRef}  />
                                        {/* <p>{errors.username?.message}</p> */}
                                    </div>
                                     <div class="form-group mb-5">
                                        <input type="password" class="form-control" placeholder="password" ef={passwordRef}/>
                                    </div>
                                    <input className="btn btn-primary px-4 py-2 w-100" type="button" value="Login" disabled={loading} onClick={
                                        // () => {
                                        //     history.push('/userdashboard')
                                        // }
                                        loginuser
                                    } />

                            </form>
                        </div>
                                

                    </div>
                   
                    
                    {/* <div className="container-lg flex-column d-flex align-items-center justify-content-center bg-white shadow br part2">
                       

                            <div className="align-items-end w-50">
                            <h5 className="fw-bold mb-3 fs-3 align-self-start">Log in</h5>
                                <form className="d-flex flex-column align-items-center" action="#" method="post">
                                <div class="form-floating mb-3">
                                    <input type="text" class="form-control" name="username" id="username" placeholder="username" />
                                    <label for="username">Username</label>
                                </div>
                                <div class="form-floating mb-5">
                                    <input type="password" class="form-control" name="password" id="password" placeholder="password"/>
                                    <label for="password">Password</label>

                                </div>
                                <input className="btn btn-primary px-4 py-2" type="button" value="Login" />
                                </form>
                                

                            </div>
                    </div> */}

                </div>
                    
                    
            {/* </div> */}

                

           
        </>
    )

}

export default Login
