 import React,{useState} from 'react'
 import axios from "axios"


 function loadScript(src) {

    return new Promise(resolve => {
        const script = document.createElement('script')
        script.src = src
        
    
        script.onload = () => {
            resolve(true)
        }

        script.onerror= () => {
            resolve(false)
        }
        document.body.appendChild(script)
    })

}




const Payment = () => {


    const [paydata, setpaydata] = useState([])

    async function dispalyRazorpay () {

        const res = await loadScript("https://checkout.razorpay.com/v1/checkout.js")

        if(!res) {
            alert("Razorpay SDK Failed to Load Check INternet Connection !")
        }

        let rdata =[]

        rata = await axios.post('http://localhost:8000/razorpay').then((res) => {
            rres.data
        })

       
        
        // const data () => {
        //     axios.get('http://localhost:8000/razorpay')
        //         .then(res => {
        //             this.setState({ setpaydata: res.data });
        //         })
        //         .catch(function (error) {
        //             console.log(error);
        //         })
        // }
    
   
        
        

        var options = {
            "key": "rzp_test_92sXJEdsMzESab", // Enter the Key ID generated from the Dashboard
            "amount": paydata.amount, // Amount is in currency subunits. Default currency is INR. Hence, 50000 refers to 50000 paise
            "currency": paydata.currency,
            "name": "Paymentor",
            "description": "Fee Transaction",
            "image": "http://localhost:8000/logo.svg",
            "order_id": paydata.id, //This is a sample Order ID. Pass the `id` obtained in the response of Step 1
            "handler": function (response){
                alert(response.razorpay_payment_id);
                alert(response.razorpay_order_id);
                alert(response.razorpay_signature)
            },
            "prefill": {
                //  name
                // "email": "gaurav.kumar@example.com",
                // "contact": "9999999999"
            }
        }


            const paymentObj = new window.Razorpay(options);
            paymentObj.open()
    }



    return (
        <>
            
            <p>Payment Page </p>

            <button className="btn btn-primary rounded-pill px-4 ms-5"
                onClick={dispalyRazorpay}
            >Pay</button>
        </>
    )
}


export default Payment
