        //         })
        //         .catch(function (error) {
        //             console.log(error);
        //         })
        // }
    
   


        


            
    }



    return (
        <>
            
            <p>Payment Page </p>
            <div className="div">
                        <p><label for="inputAmount">Password</label></p>
                        <p><input type="text" name="inputAmount" id="inputAmount" ref={inputAmount}
                        onChange={(e) => {
                            setamount(e.target.value);
                        }}/></p>
                    </div>

            <button className="btn btn-primary rounded-pill px-4 ms-5"
                onClick={dispalyRazorpay}
            >Pay</button>
        </>
    )
}


export default Payment
