import React, { useRef, useState } from "react"
 import axios from "axios"
//  import { db } from '../backend/firebase'
//  import { collection, getDocs,doc, getDoc } from "firebase/firestore"
 

 function loadScript(src) {

    return new Promise(resolve => {
        const script = document.createElement('script')
        script.src = src
        
    
        script.onload = () => {
            resolve(true)
        }

        script.onerror= () => {
            resolve(false)
        }
        document.body.appendChild(script)
    })

}




const Payment = () => {

    const inputAmount = useRef()
    const [amount, setamount] = useState()
    const [orderId, setorderId] = useState()
    const [feeamount, setfeeamount] = useState()


    // const [paydata, setpaydata] = useState([])
    // const [userDetails, setUserDetails] = useState('')
    // const userCollectionRef = collection(db ,"User");
    // const cityRef = userCollectionRef.doc('19131087');
    // const doc =  cityRef.get();
    // if (!doc.exists) {
    // console.log('No such document!');
    // } else {
    // console.log('Document data:', doc.data());
    // }


    async function dispalyRazorpay () {

        const res = await loadScript("https://checkout.razorpay.com/v1/checkout.js")

        if(!res) {
            alert("Razorpay SDK Failed to Load Check INternet Connection !")
        }



        
        axios.post('http://localhost:8000/razorpay').then((info) => {

        alert(info.data.id);
        
        setorderId(info.data.id)
        setfeeamount(info.data.amount)
        alert(orderId);
            
        })
        

    //    axios.get('http://localhost:8000/razorpay', {amt: inputAmount}).then(responce => {console.log(responce);}

    //    ).catch(err => {console.log(err);})
        
        // const data () => {
        //     axios.get('http://localhost:8000/razorpay')
        //         .then(res => {
        //             this.setState({ setpaydata: res.data });
        //         })
        //         .catch(function (error) {
        //             console.log(error);
        //         })
        // }
    
   


        


            
    }



    return (
        <>
            
            <p>Payment Page </p>
            <div className="div">
                        <p><label for="inputAmount">Password</label></p>
                        <p><input type="text" name="inputAmount" id="inputAmount" ref={inputAmount}
                        onChange={(e) => {
                            setamount(e.target.value);
                        }}/></p>
                    </div>

            <button className="btn btn-primary rounded-pill px-4 ms-5"
                onClick={dispalyRazorpay}
            >Pay</button>
        </>
    )
}


export default Payment
