        //             this.setState({ setpaydata: res.data });
        //         })
        //         .catch(function (error) {
        //             console.log(error);
        //         })
        // }
    
   
        
        

        var options = {
            "key": "rzp_test_92sXJEdsMzESab", // Enter the Key ID generated from the Dashboard
            "amount": 5000, // Amount is in currency subunits. Default currency is INR. Hence, 50000 refers to 50000 paise
            "currency": rdata.currency,
            "name": "Paymentor",
            "description": "Fee Transaction",
            "image": "http://localhost:8000/logo.svg",
            "order_id": rdata.id, //This is a sample Order ID. Pass the `id` obtained in the response of Step 1
            "handler": function (response){
                alert(response.razorpay_payment_id);
                alert(response.razorpay_order_id);
                alert(response.razorpay_signature)
            },
            "prefill": {
                //  name
                // "email": "gaurav.kumar@example.com",
                // "contact": "9999999999"
            }
        }


            const paymentObj = new window.Razorpay(options);
            paymentObj.open()
    }



    return (
        <>
            
            <p>Payment Page </p>
            <div className="div">
                        <p><label for="password">Password</label></p>
                        <p><input type="text" name="password" id="password" ref={amount}
                        onChange={(e) => {
                            setnewPass(e.target.value);
                        }}/></p>
                    </div>

            <button className="btn btn-primary rounded-pill px-4 ms-5"
                onClick={dispalyRazorpay}
            >Pay</button>
        </>
    )
}


export default Payment
